import React from 'react';
import { connect } from 'react-redux';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import AddIcon from '@material-ui/icons/AddCircleRounded';
import PlaylistAdd from '@material-ui/icons/PlaylistAdd';
import PlaylistAddCheck from '@material-ui/icons/PlaylistAddCheck';
import {
  createNode,
  clearNodes
} from '../../store/hierarchy/nodes';
import { clearCompleted } from '../../store/tasks/actions';

const Footer = ({ createNode, clearNodes, clearCompleted }) => {
  const addClick = () => {
    createNode({
      data: {
        text: ''
      }
    });
  };

  const newClick = () => {
    clearNodes();
    localStorage.setItem('score', '0')
  };

  const clearClick = () => {
    clearCompleted();
  };
  return (
    <BottomNavigation style={{ backgroundColor: 'white' }}>
      <BottomNavigationAction
        label='Add Task'
        showLabel
        onClick={addClick}
        icon={<AddIcon style={{ color:'#450B8F' }} />}
      />
      <BottomNavigationAction
        label='Clear Completed'
        showLabel
        onClick={clearClick}
        icon={<PlaylistAddCheck style={{ color:'#450B8F' }} />}
      />
      <BottomNavigationAction
        label='New List'
        showLabel
        onClick={newClick}
        icon={<PlaylistAdd style={{ color:'#450B8F' }} />}
      />
    </BottomNavigation>
  );
};

const mapDispatchToProps = dispatch => {
  return {
    createNode: payload => dispatch(createNode('tasks', payload)),
    clearNodes: () => dispatch(clearNodes('tasks')),
    clearCompleted: () => dispatch(clearCompleted())
  };
};

export default connect(
  null,
  mapDispatchToProps
)(Footer);
