import zIndex from '@material-ui/core/styles/zIndex';
import React from 'react';
import Explosion from 'react-explode/Boracay'


const divStyle = {
  backgroundColor: "white",
  borderRadius:20,
  borderWidth:2,
  borderColor:'gold',
  margin:10
}

const h3Style = {
  color:"black",
  margin: 10,
  zIndex: -1,
  padding:10
}

// const explodeStyleContainer = {
//   position: 'relative'
// }
const explodeStyle = {
  position: 'absolute',
  marginTop: '-8em',
  marginLeft: '-3em',
  zIndex: 2
}

const spacer = {
  padding: 14
}


//event listener for local storage
function Score() {
  const score = localStorage.getItem('score')
  // const [isExploding, setIsExploding] = React.useState(false);
  // if (parseInt(score) > 30) {
  //   setIsExploding(true)
  // }

  return (
    <>
      <div style={divStyle}>
        <div style={explodeStyle}>
          <Explosion size="300" delay={0} repeat={10} />
        </div>
        <h3 style={h3Style}>Score: {score}</h3>
      </div>
      <div style={spacer}></div>
    </>
  );
}

export default Score;
