import React from 'react';
import Explosion from 'react-explode/Boracay'


const divStyle = {
  backgroundColor: "white",
  borderRadius:20,
  borderWidth:2,
  borderColor:'gold',
  margin:10
}

const h3Style = {
  color:"black",
  margin:10
}


//event listener for local storage
function Score() {
  var [isExploding, setIsExploding] = React.useState(false);
  const score = localStorage.getItem('score')
  if (parseInt(score) > 30) {
    isExploding = true
  }

  return (
    <div style={divStyle}>
        <h3 style={h3Style}>
          Score: {score}

          {isExploding && (
            <div>
              <Explosion size="400" delay={0} reapeatDelay={0} repeat={1} />
            </div>
          )}
        </h3>
    </div>
  );
}

export default Score;
