import React from 'react';
import Explosion from 'react-explode/Boracay'


const divStyle = {
  backgroundColor: "white",
  borderRadius:20,
  borderWidth:2,
  borderColor:'gold',
  margin:10
}

const h3Style = {
  color:"black",
  margin:10
}

// const explodeStyleContainer = {
//   position: 'relative'
// }
const explodeStyle = {
  // position: 'absolute'
  position: 'sticky'
}


//event listener for local storage
function Score() {
  const score = localStorage.getItem('score')
  // const [isExploding, setIsExploding] = React.useState(false);
  // if (parseInt(score) > 30) {
  //   setIsExploding(true)
  // }

  return (
    <div style={divStyle}>
      <h3 style={h3Style}>Score: {score}</h3>
      <div style={explodeStyle}>
        <Explosion size="200" delay={0} repeatDelary={1} repeat={10} />
      </div>
    </div>
  );
}

export default Score;
