import React from 'react';

const divStyle = {
  backgroundColor: "white",
  borderRadius:20,
  borderWidth:2,
  borderColor:'gold',
  margin:10
}





//event listener for local storage
function Score() {

  return (
    <div style={divStyle}>
        <h3 style={{color:"black", margin:10}}>Score: {localStorage.getItem('score')}</h3>
    </div>
  );
}

export default Score;
