import React from 'react';

//event listener for local storage
function Score() {

  return (
    <div style={{backgroundColor:"white", borderRadius:20, borderWidth:2, borderColor:'gold', margin:10}}>
        <h3 style={{color:"black", margin:10}}>Score: {localStorage.getItem('score')}</h3>
    </div>
  );
}

export default Score;
